import { useState, useEffect } from 'react'
import './index.css'

// SERVICES
import loginService from './services/login'
import blogService from './services/blogs'

// COMPONENTS
import LoginForm from './components/LoginForm'
import ErrorMessage from './components/ErrorMessage'
import Blogs from './components/Blogs'
import Users from './components/Users'

// Redux implementation
import { useDispatch, useSelector } from 'react-redux'
import { addBlog, initializeBlogs } from './reducers/blogsReducer'
import { timedError } from './reducers/errorReducer'
import { timedValid } from './reducers/validReducer'
import { setTheUser } from './reducers/userReducer'

// Router Implementation
import {
  BrowserRouter as Router,
  Routes, Route, Link
} from 'react-router-dom'

const App = () => {
  const dispatch = useDispatch()
  const user = useSelector(state => state.user)

  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      dispatch(setTheUser(user))
    }
  }, [])

  useEffect(() => {
    dispatch(initializeBlogs())
  }, [dispatch])

  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username, password
      })
      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      )
      console.log(user);
      blogService.setToken(user.token)
      dispatch(setTheUser(user))
      setUsername('')
      setPassword('')

    } catch (exception) {
      dispatch(timedError('Wrong Credentials'))
      setUsername('')
      setPassword('')
    }
  }



  return (
    <div>
      <Routes>
        <Route path='/' element={<Blogs />} />
      </Routes>
      <ErrorMessage/>
      {user === null
        ? <LoginForm
          handleLogin={handleLogin}
          username={username}
          setUsername={setUsername}
          password={password}
          setPassword={setPassword}
        />
        : <Blogs />
      }
    </div>
  )
}

export default App
