import React from 'react'
import Blog from './Blog'

const BlogList = ({ blogs, userLogged, users }) => {
  const findUserBlogs = () => {
    const userLog = users.filter(user => user.username === userLogged.username)
    return userLog[0].blogs
  }
  return (
    <div>
    <h2>blogs</h2>
        <p>{userLogged.name} logged in</p>
        {findUserBlogs().map(blog => (
          <Blog key={blog.id} blog={blog} />
        ))}
        {/* {users.map(user => {
            if (user.username === userLogged.username) {
              user.blogs.map(blog => {
                return (<Blog key={blog.id} blog={blog} />)
              })
            }
        }
        )} */}
    </div>
  )
}

export default BlogList
