import React from 'react'

const CreateForm = ({ createBlog, newTitle, newAuthor, newUrl, handleTitleChange, handleAuthorChange, handleUrlChange }) => {
  return (
    <div>
        <form onSubmit={createBlog} style={{ display: 'flex', flexDirection:'column', width:'100px' }}>
        <div>
            <input 
                type='text'
                name='title'
                value={newTitle}
                onChange={handleTitleChange}
            />
        </div>

        author:
        <input 
            type='text'
            name='author'
            value={newAuthor}
            onChange={handleAuthorChange}
        />
        url:
        <input 
            type='text'
            name='url'
            value={newUrl}
            onChange={handleUrlChange}
        />
        <button type='submit' style={{ width:'60px', margin:'15px 0 15px 0'}}>create</button>
        </form>
    </div>
  )
}

export default CreateForm
