import React from 'react'
import jwt_decode from 'jwt-decode'
import { useDispatch, useSelector } from 'react-redux'
import { deleteBlogPost, updateBlogLike } from '../reducers/blogsReducer'

const Blog = ({ blog }) => {
  const dispatch = useDispatch()
  const user = useSelector(state => state.user)

  let token_decoded
  if (user) {
    token_decoded = jwt_decode(user.token)
  } else {
    token_decoded = null
  }


  const like = (event) => {
    event.preventDefault()
    const newBlog = {
      user: user ? token_decoded.id : null,
      likes: blog.likes + 1,
      author: blog.author,
      title: blog.title,
      url: blog.url,
    }

    dispatch(updateBlogLike(newBlog, blog.id))
  }

  const deletePost = (event) => {
    event.preventDefault()
    if (window.confirm(`Remove Blog ${blog.title} by ${blog.author}?`)) {
      const token_decoded = jwt_decode(user.token)
      console.log(token_decoded)
      dispatch(deleteBlogPost(blog.id, token_decoded))
    }
  }

  return (
    <>
      <div>
        {blog.title} {blog.author}
        <div>
          <div>{blog.url}</div>
          <div>likes {blog.likes} <button onClick={like} data-testid="#like">like</button></div>
          <div>{blog.author}</div>
          <button onClick={deletePost}>remove</button>
        </div>
      </div>
    </>
  )
}


export default Blog
